{
    "friendly_name": "signal_syd25_scene_1_1_initial_greeting [{{ASSET_BASE_URL}}]",
    "language": "en",
    "variables": {
        "name": "Bob",
        "dest": "SIGNAL"
    },
    "types": {
        "twilio/text": {
            "body": "Hi {{name}}! Today's the day! Welcome to Signal Transport Assist! 🚎\n\nLet's get you to {{dest}}."
        }
    }
}

{
    "friendly_name": "signal_syd25_scene_1_2_trip_plan [{{ASSET_BASE_URL}}]",
    "language": "en",
    "variables": {
        "name": "Bob",
        "dest": "SIGNAL",
        "mode_1": "Mode A and B",
        "mode_1_start": "Alpha Station",
        "mode_1_time": "30",
        "mode_1_img": "alpha.jpg",
        "mode_2": "Mode B and C",
        "mode_2_start": "Beta Station",
        "mode_2_time": "25",
        "mode_2_img": "beta.jpg",
        "mode_3": "Mode C and D",
        "mode_3_start": "Charlie Station",
        "mode_3_time": "50",
        "mode_3_img": "charlie.jpg",
        "end_loc": "{{DEMO_END_LOCATION}}",
        "end_image": "{{DEMO_END_IMAGE}}"
    },
    "types": {
        "twilio/carousel": {
            "body": "Hi {{name}}! Today's the day! Welcome to Signal Transport Assist! 🚎\n\nLet's get you to {{dest}}.",
            "cards": [
                {
                    "title": "By {{mode_1}}",
                    "body": "Taking this route from {{mode_1_start}}, it will take {{mode_1_time}} minutes.",
                    "media": "https://{{ASSET_BASE_URL}}/{{mode_1_img}}",
                    "actions": [
                        {
                            "type": "QUICK_REPLY",
                            "id": "btn_1_2_mode_1",
                            "title": "Notify me when it's time"
                        }
                    ]
                },
                {
                    "title": "By {{mode_2}}",
                    "body": "Taking this route from {{mode_2_start}}, it will take {{mode_2_time}} minutes.",
                    "media": "https://{{ASSET_BASE_URL}}/{{mode_2_img}}",
                    "actions": [
                        {
                            "type": "QUICK_REPLY",
                            "id": "btn_1_2_mode_2",
                            "title": "Notify me when it's time"
                        }
                    ]
                },
                {
                    "title": "By {{mode_3}}",
                    "body": "Taking this route from {{mode_1_start}}, it will take {{mode_3_time}} minutes.",
                    "media": "https://{{ASSET_BASE_URL}}/{{mode_3_img}}",
                    "actions": [
                        {
                            "type": "QUICK_REPLY",
                            "id": "btn_1_2_mode_3",
                            "title": "Notify me when it's time"
                        }
                    ]
                }
            ]
        },
        "twilio/text": {
            "body": "Hi {{name}}! Welcome to Signal Transport Assist! 🚎\n\nLet's get you to {{dest}}.\n\nYour trip starts from {{start_loc} and ends at {{end_loc}}.\n\nReply with REMIND to get a reminder 5 mins before your bus arrives.\n\nReply with EMAIL to get the journey plan emailed to you instead."
        }
    }
}

{
    "friendly_name": "signal_syd25_scene_2_1_reminder [{{ASSET_BASE_URL}}]",
    "language": "en",
    "variables": {
        "name": "Bob",
        "transport_mode": "Mode A",
        "transport_mode_start": "Alpha Station",
        "platform_number": "1"

    },
    "types": {
        "twilio/card": {
            "title": "Time to leave!",
            "body": "{{name}}. Your {{transport_mode}} is coming soon! Start making your way to {{transport_mode_start}}. {{transport_mode}} will leave on platform {{platform_number}}",
            "subtitle": "Are you ready to go?",
            "orientation": "HORIZONTAL",
            "actions": [
                {
                    "type": "QUICK_REPLY",
                    "id": "btn_2_1_omw",
                    "title": "On my way!"
                },
                {
                    "type": "QUICK_REPLY",
                    "id": "btn_2_1_moretime",
                    "title": "I need more time!"
                }
            ]
        },
        "twilio/text": {
            "body": "{{name}}, Your {{transport_mode}} is coming soon! Start making your way to {{transport_mode_start}}..\n\n{{transport_mode}} will leave on platform {{platform_number}}\n\nReply 'OMW' if you're ready to go or 'TIME' if you need more time."
        }
    }
}

{
    "friendly_name": "signal_syd25_scene_3_1_metro_transfer [{{ASSET_BASE_URL}}]",
    "language": "en",
    "variables": {
        "name": "Bob",
        "transfer_loc": "Point Transfer",
        "mode_1_time": "9",
        "mode_2_time": "25",
        "ferry_image": "transfer-ferry.jpg",
        "route_map_url": "https://map.trip.co/7ae9c9dsv",
        "bikeshare_image": "bikeshare.jpg",
        "bikeshare_url": "https://bikeshare.co/app"
    },
    "types": {
        "twilio/carousel": {
            "body": "You're almost at {{transfer_loc}}.",
            "cards": [
                {
                    "title": "Transfer to Ferry ⛴️",
                    "body": "Next stop, {{transfer_loc}} Metro! Hop off the train and catch a ferry to sail the harbour blue. {{mode_1_time}} minutes",
                    "media": "https://{{ASSET_BASE_URL}}/{{ferry_image}}",
                    "actions": [
                        {
                            "type": "URL",
                            "id": "btn_3_1_ferry",
                            "title": "View route map",
                            "url": "{{route_map_url}}"
                        }
                    ]
                },
                {
                    "title": "Go Green! Hire a bike 🚲",
                    "body": "You’re at {{transfer_loc}} Metro. Why walk when you can roll? Bikeshare are waiting! {{mode_2_time}} minutes",
                    "media": "https://{{ASSET_BASE_URL}}/{{bikeshare_image}}",
                    "actions": [
                        {
                            "type": "URL",
                            "id": "btn_3_1_bikeshare",
                            "title": "Book your bike",
                            "url": "{{bikeshare_url}}"
                        }
                    ]
                }
            ]
        },
        "twilio/text": {
            "body": "You're almost at {{transfer_loc}}. How would you like to proceed?.You can sail over the harbour or ride along the foreshores and enjoy the view.\n\nRoute map to Ferry: {{route_map_url}}\n\nBook bikeshare at destination: {{bikeshare_url}}"
        }
    }
}

{
    "friendly_name": "signal_syd25_scene_4_1_signal [{{ASSET_BASE_URL}}]",
    "language": "en",
    "variables": {
        "name": "Bob",
        "qrcode_image": "https://signal.twilio.com/qr/a8akvay3",
        "agenda_url": "https://signal.twilio.com/agenda"
    },
    "types": {
        "twilio/card": {
            "title": "You are at SIGNAL!",
            "body": "Welcome {{name}}! You have arrived at SIGNAL Sydney. ",
            "media": [
                "https://{{ASSET_BASE_URL}}/{{qrcode_image}}"
            ],
            "subtitle": "Show and scan your QR code to register.",
            "actions": [
                {
                    "type": "URL",
                    "id": "btn_4_1_show_agenda",
                    "title": "Show Agenda",
                    "url": "{{agenda_url}}",
                    "webview_size": "TALL"
                }
            ]
        },
        "twilio/text": {
            "body": "You are at SIGNAL! Welcome {{name}}! You have arrived at SIGNAL Sydney. \n\nRegister with QR: https://{{ASSET_BASE_URL}}/{{qrcode_image}}\n\nAgenda: {{agenda_url}}"
        }
    }
}
